<template>
    <nav class="navbar" >
        <NuxtLink :to="homeRoute">T<span>op</span>ikz</NuxtLink>
        <div class="icons">
            <span @click="infoOpen = !infoOpen"><Icon name="material-symbols:help-outline" /></span>
            <NuxtLink :to="loginRoute"><Icon name="carbon:user-avatar-filled" /></NuxtLink>
        </div>
        <Info :open='infoOpen' @onClose='infoOpen = !infoOpen'/>
    </nav>
</template>

<script>
import Info from './Info.vue';

export default {
    components: {Info},
    name: 'Navbar',
    setup() {
        return {}
    },
    data() {
        return {
            loginRoute: null,
            homeRoute: null,
            infoOpen: false, 
        }
    },
    mounted(){
        if(localStorage.localUser && localStorage.active !== 'false'){
            this.loginRoute = '/accountManagement'
            this.homeRoute = '/member'
        } else{
            this.loginRoute = '/login'
            this.homeRoute = '/'
        }
    },
    watch: {
        '$route'() {
            this.handleRouteChange();
        }
    },
    
    methods: {
        handleRouteChange() {
            if(localStorage.localUser && localStorage.active !== 'false'){
                this.loginRoute = '/accountManagement'
                this.homeRoute = '/member'
            } else{
                this.loginRoute = '/login'
                this.homeRoute = '/'
            }
        }
    }

}
</script>
